import {useState, useCallback} from "react";
import { useStream } from 'react-fetch-streams';
import { Card } from "react-bootstrap";
import classes from './Home.module.css';
import { Link } from "react-router-dom";

const Home = () => {
  const [streamData, setStreamData] = useState([{}]);
  const fetchParams = {headers: "Access-Control-Allow-Origin : *", "Content-Type": "text/html" }
  

  const onNext = useCallback(async res => {
    const streamData  = await res.json();
    setStreamData(streamData);
  }, [setStreamData]);
  useStream('http://localhost:8081/fems/all', {onNext});
 

  const renderTickets = () => {
    const ticketList = [];
    if(streamData){
    for(let i = 0 ; i < streamData.length ; i++){
      let name = `${this.streamData.firstname} ${this.streamData.lastname}`
      ticketList.push(<div>{name}</div>)
    }
  }
  }
  
  

  
  


    return <Card className={classes.homeCard}>
        <div>
          
      <Link to='/NewTicket'><span>First name: 
        {renderTickets()}</span></Link>
      </div>
    </Card>
}

export default Home;
